mapper.select:
SELECT id, name, alarm_type, state, create_time, logic_remove, alarm_content FROM alarm_mode
AND state = #{state}
AND alarm_type=#{alarmType}
AND (name LIKE concat('%',#{name},'%') OR alarm_content LIKE concat('%',#{name},'%'))
AND logic_remove = 0

mapper.update:
UPDATE alarm_mode
name=#{name,jdbcType=VARCHAR}, alarm_type=#{alarmType,jdbcType=INTEGER}, state=#{state,jdbcType=INTEGER}, create_time=#{createTime,jdbcType=TIMESTAMP}, logic_remove=#{logicRemove,jdbcType=BIT}, alarm_content=#{alarmContent,jdbcType=LONGVARCHAR},
WHERE id = #{id}
