mapper.insert:
insert into alarm_object (id, region_id, project_id, user_id,
  alarm_mode_id, contact_way, state, 
  notice_level, create_time, logic_remove
  )
values (#{id,jdbcType=BIGINT},#{regionId,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT},
  #{alarmModeId,jdbcType=BIGINT}, #{contactWay,jdbcType=VARCHAR}, #{state,jdbcType=INTEGER},
  #{noticeLevel,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{logicRemove,jdbcType=BIT}
  )

mapper.select:
SELECT a.id, a.region_id, a.project_id, a.user_id, a.alarm_mode_id, a.contact_way, a.state, a.notice_level, a.create_time, a.logic_remove, i.id AS info_id, i.name AS info_name, p.id AS pro_id, p.name AS pro_name, u.id AS acc_id, u.user_name, m.id AS mode_id, m.name AS mode_name FROM alarm_object a INNER JOIN region_info i ON i.id=a.region_id INNER JOIN region_project p ON p.id=a.project_id INNER JOIN account u ON u.id=a.user_id LEFT JOIN alarm_mode m ON m.id=a.alarm_mode_id AND m.logic_remove = 0 AND m.state = 1
AND a.state = #{state}
AND a.region_id=#{regionId}
AND a.project_id=#{projectId}
AND a.notice_level=#{noticeLevel}
AND a.alarm_mode_id=#{alarmModeId}
AND (p.name LIKE concat('%',#{keyword},'%') OR i.name LIKE concat('%',#{keyword},'%') OR u.user_name LIKE concat('%',#{keyword},'%'))
AND a.logic_remove = 0

mapper.update:
UPDATE alarm_object
region_id= #{regionId,jdbcType=BIGINT}, project_id= #{projectId,jdbcType=BIGINT}, user_id=#{userId,jdbcType=BIGINT}, alarm_mode_id=#{alarmModeId,jdbcType=BIGINT}, contact_way=#{contactWay,jdbcType=VARCHAR}, state=#{state,jdbcType=INTEGER}, notice_level=#{noticeLevel,jdbcType=INTEGER}, create_time=#{createTime,jdbcType=TIMESTAMP}, logic_remove=#{logicRemove,jdbcType=BIT},
WHERE id = #{id}
