mapper.update:
update control_room_duty
user_id =#{userId,jdbcType=BIGINT}, user_type =#{userType,jdbcType=INTEGER}, start_time =#{startTime,jdbcType=TIMESTAMP}, end_time =#{endTime,jdbcType=TIMESTAMP}, duty_date =#{dutyDate,jdbcType=DATE}, duty_type =#{dutyType,jdbcType=VARCHAR}, room_name =#{roomName,jdbcType=VARCHAR}, address =#{address,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}
where id=#{id}

mapper.select:
 select id,
  user_id as userId,
  user_type as userType,
  start_time as startTime,
  end_time as endTime,
  duty_date as dutyDate,
  duty_type as dutyType,
  room_name as roomName,
  address as address,
  create_time as createTime
from control_room_duty



  and id=#{id}


 and  user_id =#{userId,jdbcType=BIGINT}


  and user_type =#{userType,jdbcType=INTEGER}


  and start_time =#{startTime,jdbcType=TIMESTAMP}


  and end_time =#{endTime,jdbcType=TIMESTAMP}


  and duty_date =#{dutyDate,jdbcType=DATE}


 and  duty_type =#{dutyType,jdbcType=VARCHAR}


 and  room_name =#{roomName,jdbcType=VARCHAR}


 and address =#{address,jdbcType=VARCHAR}


 and create_time=#{createTime,jdbcType=TIMESTAMP}

and logic_remove=0

select id,
user_id as userId,
user_type as userType,
start_time as startTime,
end_time as endTime,
duty_date as dutyDate,
duty_type as dutyType,
room_name as roomName,
address as address,
create_time as createTime
from control_room_duty



      and id=#{id}


      and  user_id =#{userId,jdbcType=BIGINT}


      and user_type =#{userType,jdbcType=INTEGER}


      and start_time =#{startTime,jdbcType=TIMESTAMP}


      and end_time =#{endTime,jdbcType=TIMESTAMP}


      and duty_date =#{dutyDate,jdbcType=DATE}


      and  duty_type =#{dutyType,jdbcType=VARCHAR}


      and  room_name =#{roomName,jdbcType=VARCHAR}


      and address =#{address,jdbcType=VARCHAR}


      and create_time=#{createTime,jdbcType=TIMESTAMP}
