mapper.select:
SELECT c.id AS id, c.type, c.value_type, c.value_unit_type, c.value_threshold_max,c.value_threshold_min, c.heartbeat_interval, e.id AS equi_id, e.name AS equi_name, u.id AS unit_id, u.name AS unit_name FROM equipment_config c LEFT JOIN equipment_type e ON e.thing_code=c.type LEFT JOIN analogue_unit u ON u.id=c.value_unit_type
AND type = #{type}
AND value_type = #{valueType}
AND value_unit_type = #{valueUnitType}

mapper.update:
UPDATE equipment_config
type= #{type,jdbcType=INTEGER}, value_type= #{valueType,jdbcType=INTEGER}, value_unit_type=#{valueUnitType,jdbcType=INTEGER}, value_threshold_max=#{valueThresholdMax,jdbcType=REAL}, value_threshold_min=#{valueThresholdMin,jdbcType=REAL}, heartbeat_interval=#{heartbeatInterval,jdbcType=INTEGER}
WHERE id = #{id}
