mapper.select:
SELECT e.id, e.status, e.type, e.device_id, s.name AS device_name, e.first_time, e.last_time, e.description, e.report_count FROM equipment_event e LEFT JOIN equipment_state s ON s.id=e.device_id
AND e.type = #{type}
AND e.status = #{status}
AND (e.description LIKE concat('%',#{keyword},'%') OR s.name LIKE concat('%',#{keyword},'%'))

SELECT e.id AS id, e.status, e.type, e.device_id, i.name AS device_name, i.id AS equipment_id, i.name AS equipment_name, e.first_time, e.last_time, e.description, e.report_count, i.machine_room_id, r.id AS room_id, r.name AS room_name, r.project_id AS project_id , p.id AS pro_id, p.name AS pro_name, s.last_value, u.grade, (CASE WHEN s.value_threshold_max IS NULL THEN c.value_threshold_max ELSE s.value_threshold_max END) AS value_threshold_max , (CASE WHEN s.value_threshold_min IS NULL THEN c.value_threshold_min ELSE s.value_threshold_min END) AS value_threshold_min , u.alarm_threshold_max, u.alarm_threshold_min, f.id AS info_id, f.name AS info_name FROM equipment_event e INNER JOIN equipment_state s ON s.id=e.device_id LEFT JOIN equipment_config c ON c.type=s.type LEFT JOIN equipment_setup u ON u.id=s.id LEFT JOIN equipment_info i ON i.equipment_state_id=e.device_id LEFT JOIN region_computer_room r ON r.id=i.machine_room_id LEFT JOIN region_project p ON p.id=r.project_id LEFT JOIN region_info f ON f.id=p.region_id
AND e.type = #{type}
AND e.status = #{status}

mapper.update:
UPDATE equipment_event
status= #{status,jdbcType=INTEGER}, type= #{type,jdbcType=INTEGER}, device_id=#{deviceId,jdbcType=BIGINT}, first_time=#{firstTime,jdbcType=TIMESTAMP}, last_time=#{lastTime,jdbcType=TIMESTAMP}, description=#{description,jdbcType=VARCHAR}, report_count= #{reportCount,jdbcType=INTEGER},
WHERE id = #{id}
