mapper.select:
SELECT ei.id as id, ri.id as regionId, rp.id as projectId, rc.id as courtId, rs.id as streetId, rb.id as buildingId, CONCAT(IFNULL(ri.`name`,''),IFNULL(rp.`name`,''),IFNULL(rc.`name`,''),IFNULL(rs.`name`,''),IFNULL(rb.`name`,'')) as address, ei.`name` as name, ei.input_code as inputCode, et.id as typeId,et.`name` as typeName, eb.id as brandId,eb.`name` as brandName, ev.id as versionId,ev.`name` as versionName, ei.`status` as `status` FROM equipment_info ei INNER JOIN region_computer_room rr ON ei.machine_room_id = rr.id INNER JOIN region_info ri ON rr.region_id = ri.id INNER JOIN region_project rp ON rr.project_id = rp.id LEFT JOIN region_court rc ON rr.court_id = rc.id LEFT JOIN region_street rs ON rr.street_id = rs.id LEFT JOIN region_building rb ON rr.building_id = rb.id LEFT JOIN equipment_type et ON ei.type_id = et.id INNER JOIN equipment_brand eb ON ei.brand_id = eb.id LEFT JOIN equipment_version ev ON ei.version_id = ev.id
AND ri.id = #{regionId,jdbcType=BIGINT}
AND rp.id = #{projectId,jdbcType=BIGINT}
AND rc.id = #{courtId,jdbcType=BIGINT}
AND et.id = #{typeId,jdbcType=BIGINT}
AND eb.id = #{brandId,jdbcType=BIGINT}
AND ev.id = #{versionId,jdbcType=BIGINT}
AND (ei.`name` LIKE concat('%',#{keyword},'%') OR ei.input_code LIKE concat('%',#{keyword},'%'))
AND rr.logic_remove = 0 AND ri.logic_remove = 0 AND rp.logic_remove = 0 AND et.logic_remove = 0 AND eb.`status` in (0,1) AND ev.logic_remove = 0 AND ei.logic_remove = 0

SELECT ei.id as id,ei.`name` as name FROM equipment_info ei INNER JOIN region_computer_room rr ON ei.machine_room_id = rr.id AND rr.logic_remove = 0 INNER JOIN region_info ri ON rr.region_id = ri.id AND ri.logic_remove = 0 INNER JOIN region_project rp ON rr.project_id = rp.id AND rp.region_id = ri.id AND rp.logic_remove = 0 LEFT JOIN region_court rc ON rr.court_id = rc.id AND rc.region_id = ri.id AND rc.project_id = rp.id AND rc.logic_remove = 0
AND rp.id = #{projectId,jdbcType=BIGINT}
AND rc.id = #{courtId,jdbcType=BIGINT}
AND ei.logic_remove = 0
