mapper.select:
SELECT
    id,
    regionId,regionName,
    projectId,projectName,
    courtId,courtName,
    streetId,streetName,
    buildingId,buildingName,
    `name`,inputCode,
    typeId,typeName,
    brandId,brandName,
    versionId,versionName,
    `status`
FROM equipment_info_v


        AND regionId = #{regionId,jdbcType=BIGINT}


        AND projectId = #{projectId,jdbcType=BIGINT}


        AND courtId = #{courtId,jdbcType=BIGINT}



        AND typeId = #{typeId,jdbcType=BIGINT}


        AND brandId = #{brandId,jdbcType=BIGINT}


        AND versionId = #{versionId,jdbcType=BIGINT}


        AND (streetName LIKE concat('%',#{keyword},'%') OR buildingName LIKE concat('%',#{keyword},'%'))
