mapper.select:
SELECT p.id AS id,p.plan_name AS planName,p.plan_type AS planType, c.record_content AS recordContent,p.plan_status AS planStatus, p.create_time AS createTime FROM fault_detection_plan p LEFT JOIN record_by_content bc ON p.record_id=bc.record_id LEFT JOIN record_content c ON bc.content_id = c.id AND c.logic_remove=0
AND p.plan_type= #{equipmentTypeId,jdbcType=BIGINT}
AND p.plan_status= #{planStatus,jdbcType=INTEGER}
AND (p.plan_name LIKE concat('%',#{keyword},'%') OR c.record_content LIKE concat('%',#{keyword},'%'))
AND p.logic_remove = 0
