select.trim:
	fi.logic_remove=0

  and fi.id=#{id,jdbcType=BIGINT}


  and fi.region_id=#{regionId,jdbcType=BIGINT}


  and fi.project_id=#{projectId,jdbcType=BIGINT}


  and fi.create_time=#{createTime,jdbcType=TIMESTAMP}


  and fi.name like   concat('%',#{name},'%')


  and fi.logic_remove=#{logicRemove,jdbcType=BIT}


  and fi.examiner_opinion like   concat('%',#{examinerOpinion},'%')


  and fi.examiner like   concat('%',#{examiner},'%')


  and fi.examiner_date=#{examinerDate,jdbcType=TIMESTAMP}


  and fi.manager_opinion like   concat('%',#{managerOpinion},'%')


  and fi.manager_date=#{managerDate,jdbcType=TIMESTAMP}


 and fi.manager  like   concat('%',#{manager},'%')


 and fire_manager_opinion= #{fireManagerOpinion,jdbcType=VARCHAR}


  and fi.fire_manager_date=#{fireManagerDate,jdbcType=TIMESTAMP}


  and fi.fire_manager  like   concat('%',#{fireManager},'%')


  and project_manager_opinion  like   concat('%',#{projectManagerOpinion},'%')


  and fi.project_manager_date=#{projectManagerDate,jdbcType=TIMESTAMP}


  and fi.inspection_date=#{inspectionDay,jdbcType=TIMESTAMP}


 and fi.project_manager  like   concat('%',#{projectManager},'%')


  and fi.inspected_units  like   concat('%',#{inspectedUnits},'%')
