mapper.select:
select fi.id as id , fi.inspected_units as inspected_units,-- 检查部位 fi.examiner as examiner,-- 检查人 fi.inspection_day as inspection_day,-- 检查时间 FI.finish_time AS finish_time,-- 完成时间 fi.consume_time AS consume_time,-- 耗时时间 fi.inspection_type AS inspection_type,-- 检查类型 concat (",",GROUP_CONCAT(cfc.`name`),",") as fire_contents,-- 检查内容 concat (",",GROUP_CONCAT(fid.situation),",")as situation,-- 检查情况 concat (",",GROUP_CONCAT(fid.result),",") as result ,-- 处置结果 fi.status AS status -- 工单状态 from fire_inspection fi left join region_info ri on ri.id = fi.region_id left join region_project rp on rp.id = fi.region_id left join fire_inspection_detail fid on fid.fire_inspe_id = fi.id left join c_fire_contents cfc on cfc.id = fid.fire_contents_id
fi.logic_remove=0
and fi.id=#{id,jdbcType=BIGINT}
and fi.region_id=#{regionId,jdbcType=BIGINT}
and fi.project_id=#{projectId,jdbcType=BIGINT}
and fi.inspection_type=#{inspectionType,jdbcType=INTEGER}
and fi.status=#{status,jdbcType=INTEGER}
and (fi.name like concat('%',#{keyword},'%')or rp.name like concat('%',#{keyword},'%')or ri.name like concat('%',#{keyword},'%')or fi.inspected_units like concat('%',#{keyword},'%')or fi.examiner like concat('%',#{keyword},'%')or fi.name like concat('%',#{keyword},'%')or fi.name like concat('%',#{keyword},'%'))
GROUP BY fi.id
