mapper.select:
SELECT r.id AS id,type.sName,type.tName,type.oNAme,i.`name` AS equipmentName,r.situation AS situation, r.description AS description,r.record_person AS repairPerson, r.approver AS approver,r.approval_result AS approvalResult, o.state as state, r.create_time AS createTime FROM record_table r LEFT JOIN record_by_content bc ON r.id = bc.record_id LEFT JOIN record_content b ON bc.content_id=b.id LEFT JOIN equipment_info i ON r.equipment_id = i.id AND i.logic_remove=0 left JOIN (SELECT sId,sName,sParenId, tId,tName,tParenId,e1.id AS oId,e1.name AS oNAme,e1.parent_id AS oParenId FROM ( SELECT sId,sName,sParenId,e.id AS tId,e.name AS tName,e.parent_id AS tParenId FROM ( SELECT id AS sId,`name` AS sName,parent_id AS sParenId FROM equipment_type WHERE logic_remove=0 ) AS s INNER JOIN equipment_type e ON s.sParenId = e.id WHERE e.logic_remove=0 ) AS t INNER JOIN equipment_type e1 ON t.tParenId = e1.id WHERE e1.logic_remove=0) AS type ON type.sParenId = i.type_id LEFT JOIN repair_order o ON r.order_id = o.id AND o.logic_remove=0
AND r.equipment_id= #{regionId,jdbcType=BIGINT}
AND r.situation= #{situation,jdbcType=INTEGER}
AND (r.description LIKE concat('%',#{keyword},'%') OR r.record_person LIKE concat('%',#{keyword},'%') OR r.approver like concat('%',#{keyword},'%') OR r.approval_result LIKE concat('%',#{keyword},'%'))
AND r.logic_remove = 0 AND r.type = 3
