mapper.select:
SELECT c.region_id as regionId,c.project_id as projectId,c.id as id,c.organization_id as organizationId, i.name as regionName ,p.name as projectName,c.name as name,c.create_time as createTime from region_court c LEFT JOIN region_info i on i.id = c.region_id LEFT JOIN region_project p on c.project_id = p.id AND c.region_id = i.id
AND c.region_id= #{regionId,jdbcType=BIGINT}
AND c.project_id= #{ projectId,jdbcType=BIGINT}
AND (i.name LIKE concat('%',#{keyword},'%') OR p.name LIKE concat('%',#{keyword},'%') OR c.name like concat('%',#{keyword},'%'))
AND i.logic_remove = 0 AND p.logic_remove = 0 AND c.logic_remove = 0

mapper.update:
UPDATE region_court

  region_id =#{regionId},project_id =#{projectId},
  name =#{name},
  create_Time =#{createTime}

WHERE id = #{id}

UPDATE region_street

  region_id =#{regionId},
  project_id =#{projectId},


  AND region_id =#{regionId}
  AND project_id =#{projectId}
