mapper.select:
SELECT c.region_id as regionId,c.project_id as projectId,c.id as id,c.organization_id as organizationId, c.name as regionName ,p.name as projectName,i.name as name,c.create_time as createTime from region_court c LEFT JOIN region_project p on p.id = c.project_id LEFT JOIN region_info i on c.region_id = p.id
AND c.region_id= #{regionId,jdbcType=BIGINT}
AND c.project_id= #{ projectId,jdbcType=BIGINT}
AND (i.name LIKE concat('%',#{keyword},'%') OR p.name LIKE concat('%',#{keyword},'%') OR c.name like concat('%',#{keyword},'%'))
AND c.logic_remove = 0

mapper.update:
UPDATE region_court

  region_id =#{regionId},project_id =#{projectId}
  name =#{name},
  create_Time =#{createTime}

WHERE id = #{id}
