mapper.select:
SELECT s.id as id,s.`name` as name,s.organization_id as organizationId, s.region_id as regionId,i.name as regionName, s.project_id as projectId,p.name as projectName, s.court_id as courtId,c.name as courtName, s.create_time as createTime FROM region_street s LEFT JOIN region_info i ON i.id = s.region_id LEFT JOIN region_project p on s.project_id = p.id AND p.region_id = i.id LEFT JOIN region_court c on s.court_id = c.id AND c.region_id = i.id AND c.project_id = p.id
AND s.region_id= #{regionId,jdbcType=BIGINT}
AND s.project_id= #{ projectId,jdbcType=BIGINT}
AND s.court_id= #{ courtId,jdbcType=BIGINT}
AND (c.name like concat('%',#{keyword},'%'))
AND (s.name LIKE concat('%',#{keyword},'%'))
AND i.logic_remove = 0 AND p.logic_remove = 0 AND c.logic_remove = 0 AND s.logic_remove = 0

mapper.update:
UPDATE region_street


    region_id =#{regionId},project_id =#{projectId},court_id= #{courtId},

  name =#{name},
  create_Time =#{createTime}

WHERE id = #{id}

UPDATE region_street

  region_id =#{regionId},
  project_id =#{projectId},
  court_id= #{courtId},



  AND region_id =#{regionId}
  AND project_id =#{projectId}
  AND court_id= #{courtId}
