mapper.select:
SELECT s.id as id,s.`name` as name,s.organization_id as organizationId, s.region_id as regionId,(SELECT i.name from region_info i where i.id = s.region_id
AND (i.name LIKE concat('%',#{keyword},'%'))
) as regionName, s.project_id as projectId,(SELECT p.name from region_project p where p.id = s.project_id
AND (p.name LIKE concat('%',#{keyword},'%'))
) as projectName, s.court_id as courtId,(SELECT c.name from region_court c where c.id = s.court_id
AND (c.name like concat('%',#{keyword},'%'))
) as courtName, s.create_time as createTime FROM region_street s
AND s.region_id= #{regionId,jdbcType=BIGINT},
AND s.project_id= #{ projectId,jdbcType=BIGINT}
AND s.court_id= #{ projectId,jdbcType=BIGINT}
AND s.logic_remove = 0

mapper.update:
UPDATE region_street


    region_id =#{regionId},project_id =#{projectId},court_id= #{ projectId,jdbcType=BIGINT},

  name =#{name},
  create_Time =#{createTime}

WHERE id = #{id}
