mapper.insert:
insert into system_user_role (id, role_id, user_id
  )
values (#{id,jdbcType=BIGINT}, #{roleId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}
  )

mapper.select:
SELECT a.id, a.departmentName, a.telephone, a.job_number as jobNumber, a.user_name as userName, a.sex, GROUP_CONCAT( a.roleName ) as roleName FROM ( SELECT t4.NAME AS departmentName, t2.NAME AS roleName, t1.id, t1.organization_id, t1.department_id as departmentId, t1.telephone, t1.job_number, t1.sex, t1.user_name FROM account t1 LEFT JOIN system_organization t5 ON t1.organization_id = t5.user_id LEFT JOIN department t4 ON t1.department_id = t4.id LEFT JOIN system_user_role t3 ON t3.user_id = t1.id LEFT JOIN system_role t2 ON t3.role_id = t2.id
t5.id = #{organizationId,jdbcType=BIGINT}
GROUP BY t1.id, t2.id ) a
AND ( a.user_name like CONCAT('%',#{keyWords},'%') or a.departmentName like CONCAT('%',#{keyWords},'%') or a.roleName like CONCAT('%',#{keyWords},'%') or a.job_number like CONCAT('%',#{keyWords},'%') )
AND a.departmentId = #{departmentId}
AND a.sex = #{sex}
AND a.roleName is not null
AND a.roleName is null
GROUP BY a.id

mapper.update:
update system_user_role set
        role_id=#{roleId,jdbcType=BIGINT},
        user_id= #{userId,jdbcType=BIGINT}
where id= #{id,jdbcType=BIGINT}
