mapper.insert:
insert into system_user_role (id, role_id, user_id
  )
values (#{id,jdbcType=BIGINT}, #{roleId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}
  )

mapper.select:
SELECT a.id, a.deptName, a.telephone, a.job_number, a.user_name, a.sex, GROUP_CONCAT( a.roleName ) as currentRole FROM ( SELECT t4.NAME AS deptName, t2.NAME AS roleName, t1.* FROM account t1 LEFT JOIN system_organization t5 ON t1.organization_id = t5.user_id LEFT JOIN department t4 ON t1.department_id = t4.id LEFT JOIN system_user_role t3 ON t3.user_id = t1.id LEFT JOIN system_role t2 ON t3.role_id = t2.id WHERE t5.user_id = #{userId,jdbcType=BIGINT} GROUP BY t1.id, t2.id ) a GROUP BY a.id
AND a.user_name like CONCAT('%',#{keyWords},'%',)
AND t1.dpartmentId = #{dpartmentId}
AND a.sex = #{sex}

mapper.update:
update system_user_role set
        role_id=#{roleId,jdbcType=BIGINT},
        user_id= #{userId,jdbcType=BIGINT}
where id= #{id,jdbcType=BIGINT}
